mapper.select:
select * from board 
	where bd_valid = 'I' and bd_type = #{bd_type}
	order by bd_num desc
	limit #{pageStart}, #{perPageNum}

mapper.update:
update board set
		bd_title = #{dbBoard.bd_title},
		bd_contents = #{dbBoard.bd_contents}
	where bd_num = #{dbBoard.bd_num}

update board set
		bd_answer = 'Y'
	where bd_num = #{bd_num}

update board set
		bd_answer = 'N'
	where bd_num = #{bd_num}
