mapper.select:
select * from cart 
	join futsal on ca_fu_num = fu_num
	where ca_valid = 'I' and ca_num = #{ca_num}

select * from cart 
	join futsal on ca_fu_num = ca_num
	where ca_valid = 'I' and ca_num = #{num}

mapper.update:
update cart set
	ca_valid = 'D'
	where ca_num = #{ca_num}

update cart set
	ca_valid = 'D'
	where ca_num = #{ca_num}
